import { useState, useRef } from "react";
import classes from "./Checkout.module.css";

const CheckOut = (props) => {

    const isEmpty = (value) => value.trim() === '';
    const isFiveChars = (value) => value.trim().length === 5;

  const [formInputsValidity, setFormInputsValidity] = useState({
    name: true,
    street: true,
    city: true,
    postalCode: true,
  });
  const nameInputRef = useRef();
  const streetInputRef = useRef();
  const postalCodeInputRef = useRef();
  const cityInputRef = useRef();
  const onFormSubmitHandler = (e) => {
    e.preventDefault();
    const enteredName = nameInputRef.current.value;
    const enteredStreet = streetInputRef.current.value;
    const enteredPostalCode = postalCodeInputRef.current.value;
    const enteredCity = cityInputRef.current.value;

    const enteredNameIsValid = !isEmpty(enteredName);
    const enteredStreetIsValid = !isEmpty(enteredStreet);
    const enteredCityIsValid = !isEmpty(enteredCity);
    const enteredPostalCodeIsValid = isFiveChars(enteredPostalCode);

    setFormInputsValidity({
      name: enteredNameIsValid,
      street: enteredStreetIsValid,
      city: enteredCityIsValid,
      postalCode: enteredPostalCodeIsValid,
    });

    const formIsValid =
      enteredNameIsValid &&
      enteredStreetIsValid &&
      enteredCityIsValid &&
      enteredPostalCodeIsValid;

    if (!formIsValid) {
      return;
    }
  };
  const nameControlClasses = `${classes.control} ${
    formInputsValidity.name ? '' : classes.invalid
  }`;
  const streetControlClasses = `${classes.control} ${
    formInputsValidity.street ? '' : classes.invalid
  }`;
  const postalCodeControlClasses = `${classes.control} ${
    formInputsValidity.postalCode ? '' : classes.invalid
  }`;
  const cityControlClasses = `${classes.control} ${
    formInputsValidity.city ? '' : classes.invalid
  }`;

  return (
    <form className={classes.form}>
      <div className={classes.control}>
        <label>Item: </label>
        <input ref={nameInputRef} type="text"></input>
      </div>
      <div className={classes.control}>
        <label>Street: </label>
        <input ref={streetInputRef} type="text"></input>
      </div>
      <div className={classes.control}>
        <label>Postal Code: </label>
        <input ref={postalCodeInputRef} type="text"></input>
      </div>
      <div className={classes.control}>
        <label>City: </label>
        <input ref={cityInputRef} type="text"></input>
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCloseForm}>
          Cancel
        </button>
        <button className={classes.submit} onClick={onFormSubmitHandler}>
          Confirm
        </button>
      </div>
    </form>
  );
};
export default CheckOut;
