    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeals = () => {

  const { isLoading, error, sendRequest: fetchFood } = useHttp();
useEffect(() => {
  fetchFood({url:"https://food-v-102-default-rtdb.firebaseio.com/food.json"},transformFood);
}, []);
const mealsList=[]
function transformFood(data){
console.log(data);
  mealsList = data.map((meal) => (
