import { useRef } from "react";
import classes from "./Checkout.module.css"
import useInput from "../../hooks/use-input";

const CheckOut = (props) => {
    const nameInputRef=useRef()
    const streetInputRef=useRef()
    const postalCodeInputRef=useRef()
    const cityInputRef=useRef()
const onFormSubmitHandler=(e)=>{
    e.preventDefault()
    const enterdName = nameInputRef.current.value
    const enterdStreet = streetInputRef.current.value
    const enterdPostalCode = postalCodeInputRef.current.value
    const enterdCity = cityInputRef.current.value

}

  return (
    <form className={classes.form}>
      <div className={classes.control}>
        <label>Item: </label>
        <input ref={nameInputRef}type="text"></input>
      </div>
      <div className={classes.control}>
        <label>Street: </label>
        <input ref={streetInputRef}type="text"></input>
      </div>
      <div className={classes.control}>
        <label>Postal Code: </label>
        <input ref={postalCodeInputRef}type="text"></input>
      </div>
      <div className={classes.control}>
        <label>City: </label>
        <input ref={cityInputRef}type="text"></input>
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCloseForm}>Cancel</button>
        <button className={classes.submit} onClick={onFormSubmitHandler}>Confirm</button>
      </div>
    </form>
  );
};
export default CheckOut;
